import React, { createContext, useState, useEffect, useRef } from 'react';
import axios from 'axios';

export const AuthContext = createContext();


const AuthContextFunction = ({ children }) => {

    const [passcode, setPasscode] = useState();

    const injectPasscodeToRequest = (string) =>{
        setPasscode(string);

        console.log(`Hello from context`, passcode);
    }


    return (
        <AuthContext.Provider value={{ passcode, injectPasscodeToRequest }}>

            {children}

        </AuthContext.Provider>
    )

}

export default AuthContextFunction;
